import React, { useState } from 'react';

const SearchForm = (props: any) => {
  const [inputTermSearch, setInputTermSearch] = useState('');

  const inputTermSearchHandler = (event: any) => {
    console.log(event.target.value);
    props.onSaveTermSearch(event.target.value);

    setInputTermSearch(event.target.value);
  };

  return (
    <div>
      <form>
        <input
          type='search'
          value={inputTermSearch}
          onChange={inputTermSearchHandler}
        />
        <button type='submit'> Find </button>
      </form>
    </div>
  );
};

export default SearchForm;
