import React from 'react';
import AlbumList from '../Album/AlbumList';
import styles from './Disco.module.css';

const Disco = (props: any) => {
  //   console.log(`Aqui Albuns: ${props.onFilteredTerm()}`);
  // console.log(props.onFilteredTerm().term);
  const filteredDisco = props.items.filter((disco: any) => {
    return disco.artist_name === props.onFilteredTerm().term;
  });

  //   console.log(filteredDisco);
  return (
    <section>
      {props.onFilteredTerm().term !== null &&
        // <div className={styles.results}>
        `Results for: ${
          props.onFilteredTerm().term
        }`
        // </div>
      }

      <AlbumList items={filteredDisco} />
    </section>
  );
};

export default Disco;
