import React, { useState } from 'react';
import './App.css';
import Disco from './components/Disco/Disco';
import Search from './components/Search/Search';
import { Discography } from './Discography';

const App = () => {
  // const album: Album = { name: 'albumName' };
  const discos: Discography[] = [
    {
      id: 11,
      artist: 'Linkin Park',
      albuns: [
        {
          id: 1,
          name: 'Album Name1',
          source_image: './assets',
          year: 2017,
          songs: [{ title: 'Song1' }, { title: 'Song2' }],
        },
        {
          id: 2,
          name: 'Album Name2',
          source_image: './assets',
          year: 2017,
          songs: [{ title: 'Song1' }, { title: 'Song2' }],
        },
      ],
    },

    {
      id: 22,
      artist: 'Muse',
      albuns: [
        {
          id: 1,
          name: 'Album xxxx Name1',
          source_image: './assets',
          year: 2017,
          songs: [{ title: 'Song1' }, { title: 'Song2' }],
        },
        {
          id: 2,
          name: 'Albumxxxxx Name2',
          source_image: './assets',
          year: 2017,
          songs: [{ title: 'Song1' }, { title: 'Song2' }],
        },
      ],
    },

    {
      id: 33,
      artist: 'Muse',
      albuns: [
        {
          id: 5,
          name: 'Album xxxx Name5',
          source_image: './assets',
          year: 2017,
          songs: [{ title: 'Song1' }, { title: 'Song2' }],
        },
        {
          id: 6,
          name: 'Albumxxxxx Name6',
          source_image: './assets',
          year: 2017,
          songs: [{ title: 'Song1' }, { title: 'Song2' }],
        },
      ],
    },
  ];
  // console.log(discos.find((artist) => artist.artist === 'Muse'));

  const [filteredTerm, setFilteredTerm] = useState('');
  const saveTermHandler = (enteredTerm: string) => {
    setFilteredTerm((prevTerm: any) => enteredTerm);
    // console.log(filteredTerm);
  };

  return (
    <div className='App'>
      <h2>Vmusic</h2>
      <Search data={discos} onSaveTermData={saveTermHandler} />
      <Disco items={discos} onFilteredTerm={() => filteredTerm} />
    </div>
  );
};

export default App;
